import React, { Component } from 'react';
import { Field, reduxForm } from 'redux-form';

class Order extends Component {
    state = {
        firstName: '',
        lastName: '',
        address: '',
        phone: '',
    }


    render() {
        const { firstName, lastName, address, phone} = this.state;

        handleNameChnge = ({target: {value} }) => {
            this.setState({
                firstName: value,
            })
        }
        handlelastNameChnge = ({target: {value} }) => {
            this.setState({
                lastName: value,
            })
        }
        handleAddressChnge = ({target: {value} }) => {
            this.setState({
                address: value,
            })
        }
        handleNunberChnge = ({target: {value} }) => {
            this.setState({
                phone: value,
            })
        }

        return (
            <form>
                <div>
                    <label>NAME</label>
                    <Field name="firstName" component="input" type="text" value={firstName}/>
                </div>
                <div>
                    <label>SERNAME</label>
                    <Field name="lastName" component="input" type="text" value={lastName}/>
                </div>
                <div>
                    <label>ADDRESS</label>
                    <Field name="address" component="input" type="text" value={address}/>
                </div>
                <div>
                    <label>PHONE</label>
                    <Field name="phone" component="input" type="number" value={phone}/>
                </div>
                <button type="submit">Submit</button>
            </form>
        );
    }
}

Order = reduxForm({ form: 'order'})(Order);

export default Order;
