import React, { Component } from 'react';
import { Field, reduxForm } from 'redux-form';

class Order extends Component {
    constructor(props) {
        super(props);
        this.state = {
            handleSubmit: "",

        };
    }
    handleSubmitCharge = ({ target:{value} }) => {
        this.setState({
            handleSubmit: value,
        })
    }


    render() {
        const { handleSubmit } = this.props;

        return (
            <form onSubmit={handleSubmit}>
                <div>
                    <label>NAME</label>
                    <Field name="firstName" component="input" type="text" onChange={handleSubmit()}/>
                </div>
                <div>
                    <label>SERNAME</label>
                    <Field name="lastName" component="input" type="text" value={handleSubmit}/>
                </div>
                <div>
                    <label>ADDRESS</label>
                    <Field name="address" component="input" type="text" value={handleSubmit}/>
                </div>
                <div>
                    <label>PHONE</label>
                    <Field name="phone" component="input" type="number" value={handleSubmit}/>
                </div>
                <button type="submit">Submit</button>
            </form>
        );
    }
}

Order = reduxForm({ form: 'order'})(Order);

export default Order;
